import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import EventCard from './eventCard';
import './EventPage.css';


const EventPage = () => {
  const [currentTab, setCurrentTab] = useState('current');
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Dummy event data
    const dummyEvents = [
      { id: 1, title: 'Beach Cleanup', image: '/assets/junior-friends.jpg', description: 'Join us for a beach cleanup...', date: '2024-11-20' },
      { id: 2, title: 'Food Drive', image: '/images/food-drive.jpg', description: 'We are organizing a food drive...', date: '2024-10-10' },
      
        { id: 4, title: "Tree Plantation Drive", image: "data:image/jpeg;base64,", "description": "Participate in a tree plantation drive to make our environment greener.", date: "2024-12-01" },
        { id: 5, title: "Blood Donation Camp", image: "data:image/jpeg;base64,", description: "Join us in saving lives by donating blood at our camp.", date: "2024-11-25" },
        { id: 6, title: "Clothes Donation Drive", image: "/assets/clothes-drive.jpg", description: "Donate unused clothes to help those in need this winter.", date: "2024-11-30" },
        { id: 7, title: "Animal Shelter Visit", image: "/assets/animal-shelter.jpg", description: "Spend a day with adorable animals at our local shelter.", date: "2024-12-05" },
        { id: 8, title: "Book Donation Campaign", image: "/assets/book-donation.jpg", description: "Share the gift of knowledge by donating books to underprivileged children.", date: "2024-11-28" },
        { id: 9, title: "Elderly Care Program", image: "/assets/elderly-care.jpg", description: "Volunteer to spend quality time with senior citizens at the care center.", date: "2024-12-10" },
    
      
      { id: 3, title: 'Fundraiser Gala', image: 'http://bit.ly/4g69Uup', description: 'Support us in raising funds...', date: '2024-11-25' },
      { id: 8, title: 'Fundraiser Gala', image: 'http://bit.ly/4g69Uup', description: 'Support us in raising funds...', date: '2024-11-25' },
      { id: 9, title: 'Fundraiser Gala', image: 'http://bit.ly/4g69Uup', description: 'Support us in raising funds...', date: '2024-11-25' },
      { id: 10, title: 'Fundraiser Gala', image: 'http://bit.ly/4g69Uup', description: 'Support us in raising funds...', date: '2024-11-25' },
    ];
    setEvents(dummyEvents);
  }, []);

  const today = new Date();

  // Filter events by current or past
  const currentEvents = events.filter(event => new Date(event.date) >= today);
  const pastEvents = events.filter(event => new Date(event.date) < today);

  const handleAddEvent = () => {
    navigate('/addEvent');
  };

  return (
    <div className="event-page">
      <div className="tabs">
        <button className={currentTab === 'current' ? 'active' : ''} onClick={() => setCurrentTab('current')}>
          Current Events
        </button>
        <button className={currentTab === 'past' ? 'active' : ''} onClick={() => setCurrentTab('past')}>
          Past Events
        </button>
      </div>

      <div className="event-list">
        {(currentTab === 'current' ? currentEvents : pastEvents).map(event => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>

      {currentTab === 'current' && (
      <button className="add-event-button" onClick={handleAddEvent}>+ Add Event</button>
    )}
    </div>
  );
};

export default EventPage;
